CREATE TABLE USER (
                      `user_id` BIGINT NOT NULL AUTO_INCREMENT PRIMARY key  COMMENT 'ID',
                      `username` VARCHAR ( 255 ) DEFAULT NULL COMMENT '用户名',
                      `nick_name` VARCHAR ( 255 ) DEFAULT NULL COMMENT '昵称',
                      `gender` VARCHAR ( 2 ) DEFAULT NULL COMMENT '性别',
                      `phone` VARCHAR ( 255 ) DEFAULT NULL COMMENT '手机号码',
                      `email` VARCHAR ( 255 ) DEFAULT NULL COMMENT '邮箱',
                      `avatar_name` VARCHAR ( 255 ) DEFAULT NULL COMMENT '头像地址',
                      `password` VARCHAR ( 255 ) DEFAULT NULL COMMENT '密码',
                      `is_admin` bit ( 1 ) DEFAULT b'0' COMMENT '是否为admin账号',
                      `enabled` BIGINT DEFAULT NULL COMMENT '状态：1启用、0禁用',
                      `create_by` VARCHAR ( 255 ) DEFAULT NULL COMMENT '创建者',
                      `update_by` VARCHAR ( 255 ) DEFAULT NULL COMMENT '更新者',
                      `pwd_reset_time` datetime DEFAULT NULL COMMENT '修改密码的时间',
                      `create_time` datetime DEFAULT NULL COMMENT '创建日期',
                      `update_time` datetime DEFAULT NULL COMMENT '更新时间'
)COMMENT='用户表';

CREATE TABLE `sys_role` (
                            `role_id` bigint NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT 'ID',
                            `name` varchar(255) NOT NULL COMMENT '名称',
                            `description` varchar(255) DEFAULT NULL COMMENT '描述',
                            `create_by` varchar(255) DEFAULT NULL COMMENT '创建者',
                            `update_by` varchar(255) DEFAULT NULL COMMENT '更新者',
                            `create_time` datetime DEFAULT NULL COMMENT '创建日期',
                            `update_time` datetime DEFAULT NULL COMMENT '更新时间'
)  COMMENT='角色表';

CREATE TABLE `sys_users_roles` (
                                   `user_id` bigint NOT NULL COMMENT '用户ID',
                                   `role_id` bigint NOT NULL COMMENT '角色ID'
)  COMMENT='用户角色关联';



CREATE TABLE `sys_menu` (
                            `menu_id` bigint NOT NULL AUTO_INCREMENT PRIMARY key COMMENT 'ID',
                            `pid` bigint DEFAULT NULL COMMENT '上级菜单ID',
                            `sub_count` int DEFAULT '0' COMMENT '子菜单数目',
                            `type` int DEFAULT NULL COMMENT '菜单类型',
                            `title` varchar(255) DEFAULT NULL COMMENT '菜单标题',
                            `name` varchar(255) DEFAULT NULL COMMENT '组件名称',
                            `component` varchar(255) DEFAULT NULL COMMENT '组件',
                            `menu_sort` int DEFAULT NULL COMMENT '排序',
                            `icon` varchar(255) DEFAULT NULL COMMENT '图标',
                            `path` varchar(255) DEFAULT NULL COMMENT '链接地址',
                            `i_frame` bit(1) DEFAULT NULL COMMENT '是否外链',
                            `cache` bit(1) DEFAULT b'0' COMMENT '缓存',
                            `hidden` bit(1) DEFAULT b'0' COMMENT '隐藏',
                            `permission` varchar(255) DEFAULT NULL COMMENT '权限',
                            `create_by` varchar(255) DEFAULT NULL COMMENT '创建者',
                            `update_by` varchar(255) DEFAULT NULL COMMENT '更新者',
                            `create_time` datetime DEFAULT NULL COMMENT '创建日期',
                            `update_time` datetime DEFAULT NULL COMMENT '更新时间'
)  COMMENT='系统菜单';




CREATE TABLE `sys_roles_menus` (
                                   `menu_id` bigint NOT NULL COMMENT '菜单ID',
                                   `role_id` bigint NOT NULL COMMENT '角色ID'
) COMMENT='角色菜单关联';


















